import org.apache.spark.graphx._
import org.apache.spark.graphx.util._

// Create edgeTuples from the ratings - include only ratings = 1 (rating is 0 or 1, in this case)
val edgeTuples = likesDF.filter("rating = 1").map(rating => (rating(0).toString.toLong, rating(1).toString.toLong))

// Create a Graph from the edgeTuples
val graph = Graph.fromEdgeTuples(edgeTuples, "", Some(PartitionStrategy.RandomVertexCut))

// Setup parameters for PageRank
val convergenceThreshold = 0.01

// Run PageRank
val pageRank = graph.pageRank(convergenceThreshold).cache()

// Get the Top 10 Influencers
val topInfluencers = pageRank.vertices.top(10)(Ordering.by(rank => rank._2))
val topInfluencersDF = sc.parallelize(topInfluencers).toDF("itemId", "rank")
