val bucketItemsRDD = itemsRDD.flatMap(item => {
  val buckets = minHasher.buckets(item.combinedTagSignature)
  buckets.map(bucket => 
    (bucket, Seq((item))) // Emitting as Seq so we can reduce later
  )
}).reduceByKey(_ ++ _).cache()

val bucketCount = bucketItemsRDD.count()

bucketItemsRDD.map(bucketItems => bucketItems._2.size).collect()

val similarItemPairsAboveThresholdRDD = bucketItemsRDD.flatMap(bucketItems => {
  val allItemsWithinBucket = bucketItems._2

  val allItemPairsWithinBucket =
    // Cartesian product of a MUCH smaller Set of items (per bucket)
    for { 
        item1 <- allItemsWithinBucket
        item2 <- allItemsWithinBucket
    } yield (item1, item2)

  val similarItemPairsAboveThresholdWithinBucket = allItemPairsWithinBucket
    .filter(itemPair => (itemPair._1.id < itemPair._2.id)) // Filter out equal items and duplicate pairs (lower triangle)
    .map(itemPair => // Calculate LSH similarity
      (itemPair._1, itemPair._2, 
        minHasher.similarity(itemPair._1.combinedTagSignature, itemPair._2.combinedTagSignature)))
    .filter(_._3 >= minApproxLSHSimilarityThreshold) // Keep only pairs above the given threshold

  similarItemPairsAboveThresholdWithinBucket
}).cache()

