val bucketItemsRDD = itemsRDD.flatMap(item => {
  val buckets = minHasher.buckets(item.combinedTagSignature)
  buckets.map(bucket => 
    (bucket, Seq(item)) 
  )
}).reduceByKey(_ ++ _)
.cache()

val bucketSizes = bucketItemsRDD.map(bucketItems => bucketItems._2.size).collect()
val bucketCount = bucketSizes.size

val similarItemPairIdsAboveThresholdRDD = bucketItemsRDD.flatMap(bucketItems => {
  val allItemsWithinBucket = bucketItems._2

  val allItemPairsWithinBucket =
    // Cartesian product of a MUCH smaller Set of items (per bucket)
    for { 
        item1 <- allItemsWithinBucket
        item2 <- allItemsWithinBucket
        if (item1.id < item2.id) // Preserve just a triangle to avoid extra work
    } yield (item1, item2)

  val similarItemPairIdsAboveThresholdWithinBucket = allItemPairsWithinBucket
    // Calculate LSH similarity for all pairs within this bucket
    .map(itemPair => 
      (itemPair._1.id, itemPair._2.id, 
        minHasher.similarity(itemPair._1.combinedTagSignature, itemPair._2.combinedTagSignature)))
    // Keep only pairs above the given threshold
    .filter(_._3 >= minApproxLSHSimilarityThreshold) 

  similarItemPairIdsAboveThresholdWithinBucket
}).cache()

val similarItemPairIdsAboveThresholdCount = similarItemPairIdsAboveThresholdRDD.count()

// http://spark.apache.org/docs/latest/mllib-clustering.html#power-iteration-clustering-pic
import org.apache.spark.mllib.clustering.{PowerIterationClustering, PowerIterationClusteringModel}

val clustering = new PowerIterationClustering().setK(5).setMaxIterations(10)

val clusteringModel = clustering.run(similarItemPairIdsAboveThresholdRDD)
