case class MinHashTaggedItem(id: Long, title: String, combinedTagSignature: MinHashSignature) {
  override def toString: String = id + ", " + title + ", " + combinedTagSignature
}

val itemsRDD = itemsDF.select($"id", $"title", $"tags").map(row => {
  val id = row.getInt(0)
  val title = row.getString(1)
  val tagSignatures = row.getString(2).trim.split("\\|")
    .map(tag => minHasher.init(tag))
  val combinedTagSignature = minHasher.sum(tagSignatures)

  MinHashTaggedItem(id, title, combinedTagSignature)
}).cache()

itemsRDD.count()
itemsRDD.collect()

val itemBucketsDF = itemsRDD.flatMap(item => {
  val buckets = minHasher.buckets(item.combinedTagSignature)
  buckets.map(bucket => 
    (bucket, Seq((item.id, item.title, item.combinedTagSignature))) // Using Seq so we can reduce
  )
}).reduceByKey(_ ++ _).toDF()

z.show(itemBucketsDF)

