// Filter out stopwords
// The following list will be used by default if we don't specify a list:  
//   http://ir.dcs.gla.ac.uk/resources/linguistic_utils/stop_words
val stopWordsFilter = new StopWordsRemover()
  .setInputCol(tokenizer.getOutputCol)
  .setOutputCol("filteredWords")
  .setCaseSensitive(false)

val paramGrid = new ParamGridBuilder()
  .addGrid(classifier.layers, Array(Array(distinctCategoriesCount.toInt)))
  .build()
  //  .addGrid(tf.numFeatures, Array(10, 100))
//  .addGrid(idf.minDocFreq, Array(1, 10))

// Print the average metrics
val avgMetricsParamGrid = crossValidatorModel.avgMetrics

// Combine with paramGrid to see how they affect the overall metrics
val combined = paramGrid.zip(avgMetricsParamGrid)

val bestModel = crossValidatorModel.bestModel.asInstanceOf[PipelineModel]

// Explain params for each stage
val bestHashingTFNumFeatures = bestModel.stages(2).asInstanceOf[HashingTF].explainParams
val bestIDFMinDocFrequency = bestModel.stages(3).asInstanceOf[IDFModel].explainParams
//val bestWord2VecVectorSize = bestModel.stages(4).asInstanceOf[Word2VecModel].explainParams
val bestLayers = bestModel.stages(6).asInstanceOf[MultilayerPerceptronClassificationModel].explainParams

// Create Prediction Pipeline
//val predictOnDF = itemsDF.select($"description")

val predictOnDF = sqlContext.createDataFrame(Seq(
      (1, "nosql")
    )).toDF("id", "description")

val predictedResultsDF = bestModel.transform(predictOnDF)
 .select(classifier.getPredictionCol, categoryReverseIndexer.getOutputCol, stopWordsFilter.getOutputCol, classifier.getRawPredictionCol, classifier.getProbabilityCol)

z.show(predictedResultsDF)
