case class Item(id: Long, title: String, description: String, img: String, tags: Set[String]) {
  override def toString: String = id + ", " + title + ", " + tags
}

import org.apache.spark.sql.Row

// Convert from RDD[Row] to RDD[Item]
val itemsRDD = itemsDF.select($"id", $"title", $"description", $"img", $"tags").map(row =>
  Item(row.getLong(0), row.getString(1), row.getString(2), row.getString(3), row.getSeq[String](4).toSet)
)

val numHashes = 100
val minApproxJaccardSimilarityThreshold = 0.1

val numBands = MinHasher.pickBands(minApproxJaccardSimilarityThreshold, numHashes)
val minHasher = new MinHasher32(numHashes, numBands)

//val allItemPairsDF = itemsDF.join(itemsDF, itemsDF("id") === itemsDF("id"))

val allItemPairsRDD = itemsRDD.cartesian(itemsRDD)

// Filter out duplicates and preserve only the pairs with left id < right id
//val approxDistinctItemPairsRDD = allItemPairsRDD.filter(itemPair => itemPair._1.id < itemPair._2.id)
//approxDistinctItemPairsRDD.count()

// Calculate Jaccard Similarity between all item pairs (cartesian, then de-duped)
// Only keep pairs with a Jaccard Similarity above a specific threshold
val approxSimilarItemsAboveThresholdRDD = allItemPairsRDD.flatMap(itemPair => {
  val approxJaccardSim = getApproxLSHJaccardSimilarity(minHasher, itemPair._1, itemPair._2)
  if (approxJaccardSim >= minApproxJaccardSimilarityThreshold)
    Some(itemPair._1.id, itemPair._2.id, approxJaccardSim)
  else
    None
})

val approxSimilarItemPairCount = approxSimilarItemsAboveThresholdRDD.count()
approxSimilarItemsAboveThresholdRDD.collect().mkString(",")

val approxSimilarItemsAboveThresholdEdgsRDD = approxSimilarItemsAboveThresholdRDD.map(rdd => {
  Edge(rdd._1, rdd._2, rdd._3) 
})

val approxGraph = Graph.fromEdges(approxSimilarItemsAboveThresholdEdgsRDD, 0L)

