// User 12663
import org.apache.spark.sql.Row
import com.advancedspark.ml.Similarity 

// Given User: 12663
val givenUserId = 12663
val givenUserLatentFactors = model.userFactors.select($"id".as("userId"), $"features".as("userFeatures"))
  .where($"userId" === givenUserId)
  .map(row => (row.getSeq[Float](1).toArray.map(_.toDouble)))
  .collect()(0)

// Convert Array[Double] to DoubleMatrix
val givenUserLatentFactorVector = new DoubleMatrix(givenUserLatentFactors)

// Find Similar Users to the Given User
val similarUsers = model.userFactors.select($"id".as("userId"), $"features".as("userFeatures"))
  .filter($"userId" !== givenUserId)
  .map{ row =>
     val userId = row.getInt(0)
     val userLatentFactor = row.getSeq[Float](1).map(_.toDouble).toArray
     val userLatentFactorVector = new DoubleMatrix(userLatentFactor)
     val similarity = Similarity.cosineSimilarity(userLatentFactorVector, givenUserLatentFactorVector)
     (userId, similarity)
  }

// Sort and Return Top 5 Users by Similarity to Given Users
val sortedSimilarUsers = similarUsers.top(5)(Ordering.by[(Int, Double), Double] { 
  case (id, similarity) => similarity 
}).mkString("\n")

