val testFromTo = testingRatings.map { 
  case Rating(fromUserId, toUserId, rating) => (fromUserId, toUserId)
}

val predictedTestRatings = 
  model.predict(testFromTo).map { 
    case Rating(fromUserId, toUserId, rating) => ((fromUserId, toUserId), rating)
  }

val actualTestRatings = testingRatings.map { 
  case Rating(fromUserId, toUserId, rating) => ((fromUserId, toUserId), rating)
}

val RMSE = Math.sqrt(actualTestRatings.join(predictedTestRatings).map { 
  case ((fromUserId, toUserId), (r1, r2)) => {
  	val err = (r1 - r2)
  	err * err
  }
}.mean())

import org.apache.spark.sql.Row;

val recommendationsDF = model.recommendProductsForUsers(5).toDF("user_id","recommendations").cache()

case class Recommendation(fromuserid: Int, touserid: Int, confidence: Double)

val enrichedRecommendationsDF = 
  recommendationsDF.explode($"recommendations") { 
	case Row(recommendations: Seq[Row]) => recommendations.map(recommendation => 
      Recommendation(recommendation(0).asInstanceOf[Int], 
                     recommendation(1).asInstanceOf[Int], 
                     recommendation(2).asInstanceOf[Double])) 
  }.select($"fromuserid", $"touserid", $"confidence").cache()
